import React from 'react'
import { useState, useEffect } from 'react'
import { BrowserRouter as Router, Routes, Route } from "react-router-dom"

import About from './components/About'
import Home from './components/Home'
import UserProfile from './components/UserProfile'
import Navbar from './components/Navbar'
import NewRecordForm from './components/NewRecordForm'
import Login from './components/Login'
import Signup from './components/Signup'
import Footer from './components/Footer'
import Checkout from './components/Checkout'
import RecordList from './components/RecordList'
import Search from './components/Search'
import RecordDetail from './components/RecordDetail'
import FeaturedRecords from './components/FeaturedRecords'
import Cart from './components/Cart'



const App = () => {

  const [records, setRecords] = useState([]);
  const [currentUser, setCurrentUser] = useState(false);
  const [watches, setWatches] = useState([]);

  const [recordId, setRecordId] = useState(null);

  useEffect(() => {
    fetch("/authorized_user").then((r) => { 
      if (r.ok) {
        r.json().then((user) => {
          updateUser(user)
        });
      }
    });
  }, []);


  useEffect(() => {
    fetchRecords();
  }, [])

  const fetchRecords = () => {
    fetch("/records")
    .then((r) => r.json())
    .then((records) => {
      setRecords(records);
    })
  }

  const featuredRecords = () => {
    fetch("/randomized_records")
    .then((r) => r.json())
    .then((random_records) => {
      setRecords(random_records)
    })
  }

  console.log(records)

  // delete

  // const handleDeleteRecord = (id) => {
  //   if currentUser.id = record.user.id => { 
  //   fetch(`/records/${id}`,
  //   {
  //     method: "DELETE",
  //   }
  //   )
  //   .then((r) => r.json())
  //   .then(() => {
  //     setRecords((records) => records.filter((record) => record.id !== id ))
  //   }}
  // }

  // patch

  const onUpdateSelfRecord = (updatedRecord) => {
    const updatedRecords = records.map((uneditedRecord) => {
      if (uneditedRecord.id === updatedRecord.id) {
        return updatedRecord;
      } else {
        return uneditedRecord;
      }
    });
    setRecords(updatedRecords)
  }

  // complete patch + patch re-route

  const completeEditRecord = () => {
    setRecordId(null)
  }

  const enterRecordEdit = (recordId) => {
    setRecordId(recordId)
  }

  // set current user

  const updateUser = (user) => setCurrentUser(user);

  // search query

  const [search, setSearch] = useState("")

  const searchQuery = records.filter(record => {
    return(
      (record.album_name).toLowerCase().includes(search.toLowerCase()) ||
      (record.artist_name).toLowerCase().includes(search.toLowerCase()) ||
      (record.genre.genre).toLowerCase().includes(search.toLowerCase())
    )
  })

  return (
    <div>
      <Router>

        <Navbar updateUser={updateUser} currentUser={currentUser} />
        <FeaturedRecords records={records} setRecords={setRecords} featuredRecords={featuredRecords}/> 

        <Routes>

          <Route
          exact path = "/signup"
          element = {<Signup updateUser={updateUser}/>}
          />

          <Route
          exact path = "/login"
          element = {<Login updateUser={updateUser} />}
          /> 

          <Route
          exact path = "/about"
          element = {<About />}
          />

          <Route
          exact path = "/cart"
          element = {<Cart cart={cart} 
          removeCartItem={removeCartItem} 
          cartRemovalAll={cartRemovalAll} 
          cartTotal={cartTotal} 
          handleCheckoutClick={handleCheckoutClick}/>}
          />

          <Route
          exact path = "/checkout"
          element = {<Checkout cart={cart} 
          cartTotal={cartTotal} 
          cartNumber={cartNumber} 
          currentUser={currentUser} 
          setCart={setCart} 
          setCartNumber={setCartNumber} 
          setCartTotal={setCartTotal}/>}
          />

          <Route
          exact path = "/"
          element = {<Home records={records} />}
          />

          <Route
          exact path = "/records"
          element = {<RecordList records={records}/>}
          />

          <Route
          exact path = "/users/:id/edit"
          element = {<EditUserProfile />}
          />

          <Route
          exact path = "/records/:id/edit"
          element = {<EditSelfRecord />}
          />

          <Route
          exact path = "/record_details/:id"
          element = {<RecordDetail 
          currentUser={currentUser}/>}
          />

          {currentUser && (
            <Route 
            exact path = "/user_favorites"
            element = {<Favorites currentUser={currentUser} />}
            />
          )}

          {currentUser && (
            <Route
            exact path = "/new_sale"
            element = {<NewRecordForm currentUser={currentUser} />}
            /> 
          )}

          {currentUser && (
            <Route
            exact path = "/profile"
            element = {<UserProfile currentUser={currentUser} />}
            /> 
          )}

        </Routes>
          <Footer />
      </Router>
    </div>
  )
}

export default App
