  artist_name, album_cover, condition, 
  release_date, release_description, record_labels, 
  spotify_link, price, in_stock } = record

  const handleDelete = () => {
    fetch(`records/${id}`, {
      method: "DELETE",
    })
    handleDeleteRecord(id)
  }

  const handleEditClick = () => {
