import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'

const Signup = ({ updateUser }) => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
  });

  const [errors, setErrors] = useState([])
  const navigate = useNavigate();

  const { username, email, password } = formData

  const onSubmit = (e) => {
    e.preventDefault();
    const user = {
      username,
      email,
      password
    };

    fetch(`/users`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(user),
    }).then((r) => {
      if (r.ok) {
        r.json().then((user) => {
          updateUser(user)
          navigate("/")
        });
      } else {
        console.log("Your data is fucking up here")
        r.json().then((json) => setErrors(Object.entries(json.errors)))
      }
    })
  }

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({ ...formData, [name]: value });
  }

  return (
    <div>
        <div className="signup_container">
          <form onSubmit={onSubmit}>
            <ul>
              <li>
                <label className="form_header" htmlFor='username'>Username</label>
                <input
                className="input"
                type="text"
                name="username"
                id="username"
                placeholder="Enter your username"
                onChange={handleChange}
                />
              </li>
              <li>
                <label className="form_header" htmlFor='password'>Email</label>
                <input
                className="input"
                type="text"
                name="email"
                id="email"
                placeholder="Enter your email"
                onChange={handleChange}
                />
              </li>
              <li>
                <label className="form_header" htmlFor='password'>Password</label>
                <input
                className="input"
                type="password"
                name="password"
                id="password"
                placeholder="Enter a Password"
                onChange={handleChange}
                />
              </li>
            </ul>

            <div className="signup_button">
              <button
                className="button"
                type="submit"
                value="Submit">Sign up</button>
            </div>
          </form>
        </div>
    </div>
  )
}

export default Signup
