  const { id } = useParams();

  const [ record, setRecord ] = useState(null)
  useEffect(() => {
    fetch(`/records/${id}`)
    .then((r) => r.json())
    .then((record_detail) => {
      setRecord(record_detail)
    })
  }, [id])

  const { user: { username }, genre_id, album_name, 
